    @keyframes color-change {
        0% {
            color: hsl(98, 100%, 62%);
            fill: hsl(98, 100%, 62%);
        }
        50% {
            color: hsl(204, 100%, 59%);
            fill: hsl(204, 100%, 59%);
        }
        100% {
